INSERT INTO `foods` (`id`, `name`, `energy`, `protein`, `fat`, `carb`, `ingredients`, `instructions`) VALUES
	(1, 'Tatrapuder, võiga (vesi)', 98, 12, 23, 61, 'või (82%), tatar (röstitud kuivaine), vesi', 'Vesi kuumutada keemiseni, lisada tatrarangud, keeta tasasel tulel pudru valmimiseni, lisada või, maitsestada soolaga. dl = 100 g; spl = 15 g'),
	(2, 'Raguu, köögiviljadega, lihata, õliga', 62, 5, 19, 33, 'vesi, peakapsas (valge), kaalikas, mugulsibul, porgand, kartul (kooreta), rapsiõli', 'Köögiviljad puhastada, tükeldada suuremateks kuubikuteks. Kaalikad, porgandid ja sibulad kuumutada õlis, hautada väheses vedelikus poolpehmeks. Lisada juurde kuubikuteks tükeldadtud värske kapsas ning viimasena kartul, hautada koos roa valmimiseni, maitsestada. dl = 110 g'),
	(3, 'Ühepajatoit, sealihast', 105, 26, 49, 26, 'sealiha (toores), vesi, peakapsas (valge), porgand, kaalikas, kartul (kooreta)', 'Sealiha keeta soolaga maitsestatud vees. Poolpehmele lihale lisada juurde köögiviljad valmimise järjekorras: esimesena kaalikaviilud, siis terved porgandid, seejärel kapsalohud ja viimsena terved või poolitatud kartulid. Keeduleet saab kasutada roa serveerimisel kastmena. dl = 55 g'),
	(4, 'Mannapuder, võiga (vesi)', 117, 10, 21, 85, 'või (82%), manna, suhkur, vesi', 'Viia vesi keemiseni, maitsestada soola ja suhkruga, sõristada sisse manna, keeta koos pudru paksenemiseni, lisada sisse või. Serveerida koos kohe keedise või võisilmaga. dl = 100 g; spl = 15 g'),
	(5, 'Kana-kartuli-riisisupp, õliga', 79, 26, 16, 35, 'vesi, kanaliha (nahata, toores), mugulsibul, juurseller, porgand, riis (poleeritud), kartul (kooreta), rapsiõli', 'Kanast keeta puljong, kurnata. Porgandid, sellerijuur, sibulad puhastada, tükeldada kuubikuteks, kuumutada toiduõlis. Riis keeta eraldi soolaga maitsestatud vees. Kartulid koorida, tükeldada kuubikuteks. Keevasse puljongisse panna õlis kuumutatud maitseköögiviljad, keeta koos ~5 min. Seejärel lisada kartulikuubikud, viimasena keedetud sõmer riis. Supp maitsestada puljongipulbriga, vähese suhkruga. Serveerimisel lisada keedetud kana tükeldatult ja soovi korral ka hakitud maitserohelist.'),
	(6, 'Omlett, juustuga, ahjus küpsetatud', 199, 48, 142, 7, 'juust (25-26%), toores muna, piim (2,5%), rapsiõli', 'Munad kloppida lahti, lisada maitseained, piim ja riivitud juust. Küpsetada ahjus 180 C juures ~15-20 min. dl = 90 g'),
	(7, 'Lasanje, veisehakklihaga, lisatud rasvaineta (piim 2,5%)', 157, 40, 44, 72, 'piim (2,5%), keedetud nisujahust makaronid, toores veisehakkliha (7%), ketšup, nisujahu, vesi, juust (25-26%), mugulsibul', 'Valmistada eraldi hakklihakaste ja juustukaste. Hakklihakaste: Hakkliha praadida koos hakitud sibulatega, maitsestada, lisada juurde jahu, kuumutada. Lisada tomatipüree ja puljongikuubikust valmistatud puljong. Keeta kastet 5-10 min, maitsestada pipra, suhkru, pitsamaitseainega. Juustukaste: Jahu kuumutada kuivalt värvi muutuseta, jahutada, lisada keedetud jahutatud piim, keeta koos ~5 min. Lisada riivitud juust, viimistleda maitse (suhkur, sool, muskaat). Ettevalmistatud ahjuvormi asetada kihiti kastet ja lasanjeplaate nii, et viimaseks kihiks jääks juustukaste. Küpsetada rooga ahjus 170 C juures ~45 min. Küpsenud lasanje jätta veel ~30 min. sooja kohta järelvalmima enne serveerimist.'),
	(8, 'Pilaff, kanalihast', 117, 29, 32, 52, 'puljong kana puljongikuubikust, kuivatatud petersell, täistera riis, rapsiõli, konserveeritud oad, nahata kanaliha, mugulsibul, küüslauk, punane paprika', 'Kanakints maitsestada soola-pipraga, asetada kana küpsetuskotti ja u 185-kraadisesse ahju küpsema. Sibul, küüslauk ja paprika hakkida. Paksupõhjalises potis kuumutada õli ja praadida sibul ning küüslauk selles kuldseks. Seejärel lisada paprikakuubikud ja kuiv riis ning kuumutada 2–3 minutit. Konservoad nõrutada ja segada koos ürdisegu ning puljongiga riisi hulka. Segu tõsta kuumakindlasse küpsetusvormi ja küpsetada 40 minutit keskmisel kuumusel. (Et riisivorm ahjus ära ei kuivaks, asetada vormi peale tihedalt foolium.) Serveerimiseks lõigata ahjukanast pikad ribad ja asetada riisivormi peale. Juurde pakkuda vürtsikat salsat.'),
	(9, 'Värskekapsahautis, veisehakklihaga, õliga', 101, 32, 51, 15, 'vesi, veisehakkliha (7%), mugulsibul, valge peakapsas, rapsiõli', 'Hakkliha ja tükeldatud sibulad praadida õlis, maitsestada. Värske kapsas tükeldada ribadeks või kuubikuteks, hautada väheses vees poolpehmeks. Lisada praetud hakkliha ja sibulad. Hautada koos kulinaarse valmiduseni, maitsetada lõplikult. Serveerida koos keedetud kartulitega. dl = 100 g; spl = 20 g'),
	(10, 'Pirn', 41, 1, 0, 32, 'toores pirn', 'Pese ja tükelda pestud pirn'),
	(11, 'Porgand', 32, 2, 2, 22, 'toores porgand', 'Pese ja tükelda porgand'),
	(12, 'Kaalikas', 36, 5, 1, 24, 'kaalikas', 'Pese ja koori kaalikas. Tükelda.'),
	(13, 'Banaan', 91, 5, 3, 81, 'banaan', 'Koori ja tükelda banaan'),
	(14, 'Paprika', 28, 4, 2, 18, 'paprika', 'Pese ja viiluta paprika.'),
	(15, 'Banaanikeefir', 81, 10, 17, 53, 'banaan, mahlakonsentraat suhkruga, suhkur, keefir (2,5%)', 'Segada keefir, suhkur ja mahlakontsentraat. Banaanid koorida, tükeldada väiksemaks, püreerida saumikseriga koos osa keefiriga. Ühendada ülejäänud maitsestatud keefir ja püreeritud banaanid, vahustada. Serveerida kohe. Kaunistuseks võib peale (sisse) lisada maasikaid või kirsse.');
